data.wp_custom_css:
.preloader {
   position: fixed;
   top: 0;
   left: 0;
   right: 0;
   bottom: 0;
   background-color: #000000;
   z-index: 100000;
   height: 100%;
   width: 100%;
   overflow: hidden !important;
}
.preloader .status {
   width: 100px;
   height: 100px;
   position: absolute;
   left: 50%;
   top: 50%;
   background-image: url(https://architecture.hype-recipe.com/wp-content/uploads/2019/03/Double-Ring-2.6s-200px.gif);
   background-repeat: no-repeat;
   background-position: center;
   -webkit-background-size: cover;
   background-size: cover;
   margin: -50px 0 0 -50px;
}
	
	


	
	
	
	
	
	

@media all and (min-width: 981px) {
    .ds-scroll-slider {
        position: relative;
        overflow: hidden;
        display: block;
        margin: 0;
        padding: 0;
        min-height: 100vh;
    }
    .ds-scroll-wrapper {
        position: relative;
        display: -webkit-box;
        display: -ms-flexbox;
        display: flex;
        -ms-flex-wrap: nowrap;
        flex-wrap: nowrap;
        -webkit-box-align: center;
        -ms-flex-align: center;
        align-items: center;
        min-height: 100vh;
        -webkit-box-sizing: border-box;
        box-sizing: border-box;
        -webkit-touch-callout: none;
        -webkit-user-select: none;
        -moz-user-select: none;
        -ms-user-select: none;
        user-select: none;
        -ms-touch-action: pan-y;
        touch-action: pan-y;
        -webkit-tap-highlight-color: transparent;
    }
    .ds-scroll-slide {
        -webkit-box-flex: 1;
        -ms-flex: 1 0 100vw;
        flex: 1 0 100vw;
        max-width: 100vw;
        display: -webkit-box;
        display: -ms-flexbox;
        display: flex;
        -webkit-box-pack: center;
        -ms-flex-pack: center;
        justify-content: center;
        -webkit-box-align: center;
        -ms-flex-align: center;
        align-items: center;
        height: 100%;
    }
}


/*Remove extra padding on smaller screens*/

@media all and (max-width: 980px) {
    .scrollmagic-pin-spacer {
        padding-bottom: 0 !important;
    }
}
